svg.script:
"use strict";

var args = flexo.get_args();
if (args.href) {
  var context = bender.create_context();
  flexo.listen(context.ownerDocument, "@error", function (e) {
    alert("Error loading {0}: {1}.".fmt(e.uri, e.message));
  });
  context.appendChild(context.$("use", args));
} else {
  alert("Nothing to run. Please specify a Bender component to load with the " +
    "href URL parameter.");
}
